/*Table structure for table `order_returns` */

DROP TABLE IF EXISTS `order_returns`;

CREATE TABLE `order_returns` (
  `oid` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `customerid` bigint(20) DEFAULT 0 COMMENT '用戶ID',
  `return_type` varchar(20) DEFAULT NULL,
  `ship_type` varchar(20) DEFAULT NULL,
  `shipping_name` varchar(50) DEFAULT '' COMMENT '收貨人姓名',
  `shipping_sex` varchar(5) DEFAULT NULL,
  `shipping_mobile` varchar(15) DEFAULT NULL,
  `shipping_tel1` varchar(10) DEFAULT NULL,
  `shipping_tel2` varchar(15) DEFAULT '' COMMENT '收貨人電話',
  `shipping_email` varchar(30) DEFAULT NULL,
  `shipping_city` varchar(30) DEFAULT NULL,
  `shipping_area` varchar(30) DEFAULT NULL,
  `shipping_address` varchar(255) DEFAULT '' COMMENT '收貨地址',
  `shipping_zipcode` varchar(25) DEFAULT '',
  `LogisticsType` varchar(64) DEFAULT '',
  `LogisticsSubType` varchar(64) DEFAULT '',
  `return_express_status` varchar(20) DEFAULT '',
  `RtnMerchantTradeNo` varchar(64) DEFAULT '' COMMENT '綠界科技逆物流',
  `RtnOrderNo` varchar(64) DEFAULT '' COMMENT '退貨編號',
  `ship_time` varchar(20) DEFAULT NULL,
  `bank_name` varchar(50) DEFAULT NULL,
  `bank_num` varchar(30) DEFAULT NULL,
  `remark` text DEFAULT NULL COMMENT '會員備註',
  `admin_remark` text DEFAULT NULL COMMENT '管理員備註',
  `update_time` int(10) DEFAULT 0 COMMENT '更新日期',
  `create_time` int(10) DEFAULT 0 COMMENT '創建日期',
  PRIMARY KEY (`oid`),
  KEY `user_id_create_date_order_status` (`customerid`,`create_time`)
) ENGINE=InnoDB AUTO_INCREMENT=15243592254308 DEFAULT CHARSET=utf8 COMMENT='退換貨';

